
public class whileSumNaturals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1,sum=0, n=5;
		while(i <= n)
		{
			sum = sum + i;
			i++;
		}
		System.out.println("Sum of First 5 Natural numbers :" + sum);
		
			
		// TODO Auto-generated method stub

	}

}
